import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import axios from 'axios'
import { connect } from 'react-redux'
import { useHistory } from "react-router-dom"
import cogoToast from 'cogo-toast';
import { withRouter } from 'react-router-dom';
import { setUser } from '../../Redux/actions/loginActions'
class Login extends Component {
    constructor(props) {
        super(props)

        const currentitem = {
            username: '',
            password: ''
        }
        this.state = {
            currentitem
        }
    }

    componentDidMount() {      

        this.props.setUser(this.state.currentitem);
        console.log('this.state.currentitem: ', this.state.currentitem);
        
        this.setState({ currentitem: this.props.user })        
       
    }

    handlesubmit = () => {

        console.log('this.state.currentitem: ', this.state.currentitem);
        axios.post(process.env.REACT_APP_BACKEND_URL + '/api/user/login', this.state.currentitem)
            .then(res => {
                localStorage.setItem('token', res.data)
                this.props.history.push("/dashboard")
            })
            .catch(err => {
                cogoToast.error('Kaydedilirken Hata Alındı', this.toastoptions)

            })
    }

    handleChangeInput = (e) => {
        const newdata = { ...this.state.currentitem }
        newdata[e.target.id] = e.target.value
        this.setState({ currentitem: newdata })
        this.props.selectedStock(this.state.currentitem);
    }

    toastoptions = {
        hideAfter: 5,
        position: 'top-right',
        heading: 'Dil Güncelleme'
    }

    render() {
        return (
            <div className='loginpage'>
                <div className="d-flex align-items-stretch auth auth-img-bg h-100">
                    <div className="row flex-grow">
                        <div className="col-lg-6 d-flex align-items-center justify-content-center">
                            <div className="auth-form-transparent text-left p-3">
                                <div className="brand-logo">
                                    <img src={require("../../../assets/images/logo.svg")} alt="logo" />
                                </div>
                                <h4>Hoşgeldiniz!</h4>
                                <h6 className="font-weight-light">Uygulamamıza HoşGeldiniz</h6>
                                <form className="pt-3" onSubmit={this.handlesubmit}>
                                    <div className="form-group">
                                        <label>Kullanıcıadi</label>
                                        <div className="input-group">
                                            <div className="input-group-prepend bg-transparent">
                                                <span className="input-group-text bg-transparent border-right-0">
                                                    <i className="mdi mdi-account-outline text-primary"></i>
                                                </span>
                                            </div>
                                            <input type="text" className="form-control form-control-lg border-left-0" id="username" placeholder="Kullanıcı Adı"
                                                onChange={this.handlesubmit} value={this.state.currentitem.username} />
                                        </div>
                                    </div>
                                    <div className="form-group">
                                        <label>Password</label>
                                        <div className="input-group">
                                            <div className="input-group-prepend bg-transparent">
                                                <span className="input-group-text bg-transparent border-right-0">
                                                    <i className="mdi mdi-lock-outline text-primary"></i>
                                                </span>
                                            </div>
                                            <input type="password" className="form-control form-control-lg border-left-0" id="password" placeholder="Şifre"
                                                onChange={this.handlesubmit} value={this.state.currentitem.password} />
                                        </div>
                                    </div>
                                    <div className="my-2 d-flex justify-content-between align-items-center">
                                        <div className="form-check">
                                            <label className="form-check-label text-muted">
                                                <input type="checkbox" className="form-check-input" />
                                                <i className="input-helper"></i>
                                                Beni Giriş Yapılı Tut
                                            </label>
                                        </div>
                                        <a href="!#" onClick={event => event.preventDefault()} className="auth-link text-muted">Şifreyi Unuttum?</a>
                                    </div>
                                    <div className="my-3">
                                        <button className="btn btn-block btn-primary btn-lg font-weight-medium auth-form-btn" >Giriş Yap</button>
                                    </div>
                                    <div className="text-center mt-4 font-weight-light">
                                        Hesabın Yok mu? <Link to="/Register" className="text-primary">Kayıt Ol</Link>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="col-lg-6 login-half-bg d-flex flex-row">
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    stock: state.user
})
const mapDispatchToProps = { setUser }

export default connect(mapStateToProps, mapDispatchToProps)(withRouter(Login))
