import React, { Component } from 'react'
import { connect } from 'react-redux'
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';
import ToolkitProvider, { Search } from 'react-bootstrap-table2-toolkit';
import axios from 'axios';
import { setStocks } from '../../Redux/actions/stockActions';



export class StockListining extends Component {
    constructor(props) {
        super(props)
        const currentitem = [{
            id: 0,
            stokkod: '',
            stokadı: '',
            miktar: 0,
            birim: '',
            alışfiyat: 0.0,
            satışfiyat: 0.0,
            kdv: '',
            İskonto: 0
        }]
        this.state = { currentitem };
    }

    componentDidMount() {
        this.getStocks()
    }
   
    getStocks = async () => {
        const response = await axios.get('http://localhost:18598/api/Stok/GetStokAll',
            { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } })
            .catch(error => {
                if (error.response.status == '401') {
                    this.props.history.push("/Login")
                }
            })
        if (response != undefined) {
            this.props.selectedStock(response.data);
            this.setState({ currentitem: this.props.stock })
        }
    };

    render() {
        return (
            <div>
                <div className="row datatable">
                    <div className="col-12">
                        <div className="card">
                            <div className="card-body">
                                <h4 className="card-title">Stock Table</h4>
                                <div className="row">
                                    <div className="col-12">
                                        <ToolkitProvider
                                            keyField="id"
                                            bootstrap4
                                            data={stocks}
                                            columns={columns}
                                            search
                                        >
                                            {
                                                props => (
                                                    <div>
                                                        <div className="d-flex align-items-center">
                                                            <p className="mb-2 mr-2">Search in table:</p>
                                                            <SearchBar {...props.searchProps} />
                                                        </div>
                                                        <BootstrapTable
                                                            defaultSorted={defaultSorted}
                                                            pagination={paginationFactory()}
                                                            {...props.baseProps}
                                                            wrapperClasses="table-responsive"
                                                        />
                                                    </div>
                                                )
                                            }
                                        </ToolkitProvider>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    allStocks: state.allStocks
})

const mapDispatchToProps = { setStocks }

export default connect(mapStateToProps, mapDispatchToProps)(withRouter(StockListining))
