            <div className="card-body">
              <h4 className="card-title">Stock Table</h4>
              <div className="row">
                <div className="col-12">
                  <ToolkitProvider
                    keyField="id"
                    bootstrap4
                    data={maindata}
                    columns={columns}
                    search
                  >
                    {
                      props => (
                        <div>
                          <div className="d-flex align-items-center">
